mapper.select:
SELECT fd.department_id AS departmentId, fd.department_no AS dTempNo, fd.department_no AS departmentNo, fd.department_name AS departmentName, fd.parent_no AS parentNo, fd.property_no AS propertyNo, fp.property_name AS propertyName, fd.room_no AS roomNo, fd.telephone, fd.principal, fd.member_num AS memberNum, fd.is_down AS isDown, fd.create_by AS createBy, fd.create_time AS createTime, fd.update_by AS updateBy, fd.update_time AS updateTime FROM t_f_department fd,t_f_property fp WHERE fp.property_no=fd.property_no
and fd.department_id in
#{id}
and fp.property_no=#{name}
order by fd.create_time desc
